import org.apache.log4j.Logger;
    Logger logger = Logger.getLogger("DExpertslog");
    logger.info("Log from the Rule: Firstname="+object.getAttribute("firstname"));
	String firstname =object.getAttribute("firstname");
	String lastname = object.getAttribute("lastname");

	object.setAttribute("firstname",firstname.substring(0,1).toUpperCase()+firstname.substring(1).toLowerCase());
	object.setAttribute("lastname",lastname.substring(0,1).toUpperCase()+lastname.substring(1).toLowerCase());

	  logger.info("Log from the Rule: Gender="+object.getAttribute("gender")); 
	String shortFormGender=object.getAttribute("gender");
	 if(shortFormGender.equals("f")){
		 object.setAttribute("gender","female");
	 }
          if (shortFormGender.equals("m")){
		 object.setAttribute("gender","male");
	 }
	 return object;
